mapper.update:
UPDATE FLASH_BOARD SET
FTITLE = #{fTitle}, FPEOPLE = #{fPeople}, FMEETDATE = #{fMeetDate}, FMEETTIME = #{fMeetTime},
FCONTENT = #{fContent}, FMAPTITLE = #{fMapTitle}, FMAPX = #{fMapX}, FMAPY = #{fMapY}
WHERE FNO = #{fNo}

mapper.delete:
UPDATE FLASH_BOARD
SET FSTATUS = 'N'
WHERE FNO = #{fNo}
