mapper.select:
SELECT * FROM PLANNER_PART
WHERE PNO = #{pNo}
ORDER BY STARTTIME ASC

mapper.update:
UPDATE PLANNER

	DESCRIBE=#{describe},
	STATUS=#{status}

WHERE PNO = #{pNo}

UPDATE PLANNER_PART
STARTTIME=#{startTime}, ENDTIME=#{endTime}, MAPTITLE=#{mapTitle}, CATEGORY=#{category}, MAPX=#{mapX}, MAPY=#{mapY}, MONEY=#{money}, REASON=#{reason}
WHERE PPNO = #{ppNo}

mapper.select:
SELECT CATEGORY, COUNT(*) COUNT FROM PLANNER_PART
WHERE PNO = #{pNo}
GROUP BY CATEGORY
